body {
	display: flex;
	justify-content: center;
	align-items: center;
	font-family: 'roboto', arial, sans-serif;
	font-size: 1.5rem;
	min-height: 100vh;
	background-color: #fff;
}

.calculator {
	display: grid;
	grid-template-columns: 25% 25% 25% 25%;
	width: 400px;
	height: 700px;
	-webkit-box-shadow: 10px 10px 26px 10px #DCE1E6;
-moz-box-shadow: 10px 10px 26px 10px #DCE1E6;
box-shadow: 10px 10px 26px 10px #DCE1E6;
}

.button-area {
	display: grid;

}

.button {
	background-color: #fff;
	color: #2C4053;
	display: flex;
	align-items: center;
	justify-content: center;
	font-weight: 400;
	border-left: solid 1px #DCE1E6;
	border-bottom: solid 1px #DCE1E6;
	cursor: pointer;
}

.screen {
	position: relative;
	background-color: #fff;
	text-align: right;
	grid-row: 1/3;
	grid-column: 1/5;
	padding:  0 0.25em;
	border-bottom: solid 1px #fff;
	-webkit-box-shadow: 0px 3px 3px 0px rgba(196,203,211,1);
    -moz-box-shadow: 0px 3px 3px 0px rgba(196,203,211,1);
    box-shadow: 0px 3px 3px 0px rgba(196,203,211,1);	
}

.screen-content-wrapper {
	height: 100%;
	display: flex;
	flex-direction: column;
	justify-content: flex-end;
}

.button.right {
	background-color: #2C4053;
	color: #fff;
}

.button.top {
	background-color: #f4f9ff;

}

#equals {
	background-color: #4fcbcb;
	color: #fff;
	grid-column: 3/5;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;	
}

.input {
	color: #abb4bc;
	display: block;
	font-size: 1.5rem;
	min-height: 28px;
	letter-spacing: 1px;
}

.output {
	font-size: 4.5rem;
	min-height: 95px;
}
