body {
    display: flex;
    justify-content: center;
    align-items: center;
    font-family: 'roboto', arial, sans-serif;
    font-size: 1.5rem;
    min-height: 100vh;
    background-color: #fff;
}

.calculator {
    width: 400px;
    box-shadow: 10px 10px 26px 10px #dce1e6;

    &__screen {
        position: relative;
        background-color: #fff;
        text-align: right;
        grid-row: 1/3;
        grid-column: 1 / -1;
        padding: 0 0.25em;
        border-bottom: solid 1px #fff;
        box-shadow: 0px 3px 3px 0px rgba(196, 203, 211, 1);
    }

    &__buttons {
        display: grid;
        grid-template-columns: repeat(4, 1fr);
    }

    &__button {
        background-color: #fff;
        color: #2c4053;
        display: flex;
        align-items: center;
        justify-content: center;
        font-weight: 400;
        border: none;
        border-left: solid 1px #dce1e6;
        border-bottom: solid 1px #dce1e6;
        cursor: pointer;
        line-height: 3;
        font-size: 1.5rem;

        &.right {
            background-color: #2c4053;
            color: #fff;
        }

        &.top {
            background-color: #f4f9ff;
        }

        &#equals {
            background-color: #4fcbcb;
            color: #fff;
            grid-column: 3/5;
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        &:active {
            background: #f4f9ff;
        }

        &.input {
            color: #abb4bc;
            display: block;
            font-size: 1.5rem;
            min-height: 28px;
            letter-spacing: 1px;
        }
    }
}






.screen-content-wrapper {
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
}


.output {
    font-size: 4.5rem;
    min-height: 95px;
}
